context.value:
(
    sm: (
        form-rows-spacing: $kendo-form-sm-rows-spacing
    ),
    md: (
        form-rows-spacing: $kendo-form-md-rows-spacing
    ),
    lg: (
        form-rows-spacing: $kendo-form-lg-rows-spacing
    )
)

context.code:
@if ( k-map-has-key( $_kendo-data-uris, $name ) ) {
    @warn "Attempt to redefine data URI of file `#{$name}`.";
}

$_kendo-data-uris: k-map-merge( $_kendo-data-uris, ( $name: $content ) ) !global;
